mapper.select:
SELECT a.id,a.name,b.dirname, a.createtime FROM i_fileversion a
JOIN i_dir b ON a.dirid=b.id
WHERE a.dirid=#{dirid}

SELECT a.id,a.name,b.dirname,a.createtime,a.versionnum FROM i_fileversion a
JOIN i_dir b ON a.dirid=b.id
WHERE dirid=#{param1} LIMIT #{param2},#{param3}

SELECT a.id,a.name,a.description,a.createtime,a.download,a.parentid,a.type FROM i_file a
JOIN i_fileversion b ON a.parentid=b.id
WHERE a.parentid=#{param3} AND level=1 order by a.id desc LIMIT #{param1},#{param2}
